mapper.delete:

update productlabor_cost
set status = -1
where id = #{id,jdbcType=INTEGER}

mapper.update:

update productlabor_cost
set pid = #{box.id,jdbcType=INTEGER},
  pielabor_price = #{pielaborPrice,jdbcType=DOUBLE},
  tonlabor_price = #{tonlaborPrice,jdbcType=DOUBLE},
  status = #{status,jdbcType=INTEGER},date=#{date,jdbcType=TIMESTAMP}
where id = #{id,jdbcType=INTEGER}
